mapper.select:
select
  userId, userName, userStatue, userNum, userPwd, userRight, phone, address, qqCode, remark
from album_db_user


        and userId = #{userId}


        and userName = #{username}


        and userStatue = #{userStatue}


        and userNum = #{userNum}


        and userPwd = #{userPwd}


        and userRight = #{userRight}


        and phone = #{phone}


        and address = #{address}


        and qqCode = #{qqCode}


        and remark = #{remark}

select count(1)
from album_db_user


        and userId = #{userId}


        and userName = #{username}


        and userStatue = #{userStatue}


        and userNum = #{userNum}


        and userPwd = #{userPwd}


        and userRight = #{userRight}


        and phone = #{phone}


        and address = #{address}


        and qqCode = #{qqCode}


        and remark = #{remark}
